void expand_pixel_in_array(int idx, float* input)
{
    // 中心点和上下左右四个像素的索引
    int neighbors_idx[5] = { idx, idx - 1, idx + 1, idx - 28, idx + 28 };

    // 遍历中心点及其邻居并设置 input 对应位置为 1.0f
    for (int i = 0; i < 5; ++i)
    {
        if (neighbors_idx[i] >= 0 && neighbors_idx[i] < 28 * 28)
        {
            input[neighbors_idx[i]] = 1.0f;  // 设置为 1.0 表示该块中有绘制
        }
    }
}

