#define INPUT_SIZE 784  // 28x28的图像展平后的大小
#define CONV1_OUT_CHANNELS 4
#define CONV2_OUT_CHANNELS 4
#define FC1_OUTPUT_SIZE 16
#define OUTPUT_SIZE 10

// 卷积层实现
void conv2d(float* input, float* output, const float* weight, const float* bias, int in_height, int in_width, int in_channels, int out_channels, int kernel_size, int stride, int padding) {
    int out_height = (in_height - kernel_size + 2 * padding) / stride + 1;
    int out_width = (in_width - kernel_size + 2 * padding) / stride + 1;

    for (int oc = 0; oc < out_channels; oc++) {
        for (int oh = 0; oh < out_height; oh++) {
            for (int ow = 0; ow < out_width; ow++) {
                float sum = bias[oc];
                for (int ic = 0; ic < in_channels; ic++) {
                    for (int kh = 0; kh < kernel_size; kh++) {
                        for (int kw = 0; kw < kernel_size; kw++) {
                            int ih = oh * stride + kh - padding;
                            int iw = ow * stride + kw - padding;
                            if (ih >= 0 && ih < in_height && iw >= 0 && iw < in_width) {
                                sum += input[(ic * in_height + ih) * in_width + iw] *
                                    weight[((oc * in_channels + ic) * kernel_size + kh) * kernel_size + kw];
                            }
                        }
                    }
                }
                output[(oc * out_height + oh) * out_width + ow] = sum;
            }
        }
    }
}

// ReLU激活函数
void relu(float* input, int size) {
    for (int i = 0; i < size; i++) {
        if (input[i] < 0) {
            input[i] = 0;
        }
    }
}

// 最大池化层
void max_pool2d(float* input, float* output, int in_height, int in_width, int channels, int pool_size, int stride) {
    int out_height = (in_height - pool_size) / stride + 1;
    int out_width = (in_width - pool_size) / stride + 1;

    for (int c = 0; c < channels; c++) {
        for (int oh = 0; oh < out_height; oh++) {
            for (int ow = 0; ow < out_width; ow++) {
                float max_val = -1e10;
                for (int ph = 0; ph < pool_size; ph++) {
                    for (int pw = 0; pw < pool_size; pw++) {
                        int ih = oh * stride + ph;
                        int iw = ow * stride + pw;
                        float val = input[(c * in_height + ih) * in_width + iw];
                        if (val > max_val) {
                            max_val = val;
                        }
                    }
                }
                output[(c * out_height + oh) * out_width + ow] = max_val;
            }
        }
    }
}

// 全连接层实现
void fully_connected(float* input, float* output, const float* weight, const float* bias, int input_size, int output_size) {
    for (int i = 0; i < output_size; i++) {
        output[i] = bias[i];
        for (int j = 0; j < input_size; j++) {
            output[i] += input[j] * weight[i * input_size + j];
        }
    }
}

