// 预测函数
void predict(float* input, float* output) {
    //lcd_show_string(0, 0, 200, 16, 16, "pred", RED);

    // 动态分配内存给卷积层1的输出
    float* conv1_output = (float*)malloc(4 * 28 * 28 * sizeof(float));
    if (!conv1_output) {
        // 内存分配失败，处理错误
        return;
    }
    conv2d(input, conv1_output, conv1_weight, conv1_bias, 28, 28, 1, 4, 3, 1, 1);
    relu(conv1_output, 4 * 28 * 28);

    // 动态分配内存给池化层1的输出
    float* pool1_output = (float*)malloc(4 * 14 * 14 * sizeof(float));
    if (!pool1_output) {
        free(conv1_output);
        return;
    }
    max_pool2d(conv1_output, pool1_output, 28, 28, 4, 2, 2);

    // 释放卷积层1的输出内存
    free(conv1_output);

    // 动态分配内存给卷积层2的输出
    float* conv2_output = (float*)malloc(4 * 14 * 14 * sizeof(float));
    if (!conv2_output) {
        free(pool1_output);
        return;
    }
    conv2d(pool1_output, conv2_output, conv2_weight, conv2_bias, 14, 14, 4, 4, 3, 1, 1);
    relu(conv2_output, 4 * 14 * 14);

    // 释放池化层1的输出内存
    free(pool1_output);

    // 动态分配内存给池化层2的输出
    float* pool2_output = (float*)malloc(4 * 7 * 7 * sizeof(float));
    if (!pool2_output) {
        free(conv2_output);
        return;
    }
    max_pool2d(conv2_output, pool2_output, 14, 14, 4, 2, 2);

    // 释放卷积层2的输出内存
    free(conv2_output);

    // 动态分配内存给全连接层1的输出
    float* fc1_output = (float*)malloc(16 * sizeof(float));
    if (!fc1_output) {
        free(pool2_output);
        return;
    }
    fully_connected(pool2_output, fc1_output, fc1_weight, fc1_bias, 4 * 7 * 7, 16);
    relu(fc1_output, 16);

    // 释放池化层2的输出内存
    free(pool2_output);

    // 全连接层2
    fully_connected(fc1_output, output, fc2_weight, fc2_bias, 16, 10);

    // 释放全连接层1的输出内存
    free(fc1_output);
}




